import './App.css';
import React, { Component } from 'react';
import axios from 'axios';



class App extends Component {
  state = {
    selectedFile: null,
    fileUploadedSuccesfully: false
  }

  onFileChange = event => {
    this.setState({selectedFile: event.target.files[0]});
  }

  onFileUpload = async () => {
    /*
    const formData = new FormData();
    formData.append(
      "demo file",
      this.state.selectedFile,
      this.state.selectedFile.name
    )
    //chiamare api
    console.log(formData)
    this.setState({selectedFile: null});
    this.setState({fileUploadedSuccesfully: true});
    */
    const formData = new FormData();
    formData.append('csv', selectedFile);

    try {
      const response = await axios.post('URL_DEL_TUO_API_GATEWAY', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Gestisci la risposta dal backend
      console.log(response.data);
    } catch (error) {
      // Gestisci gli errori
      console.error('Errore durante il caricamento del file:', error);
    }
  }

  fileData = () => {
    if(this.state.selectedFile){
      return(
        <div>
        <h2>File Details:</h2>
        <p>File Name: {this.selectedFile.name}</p>
      </div>
      );
    }else if(this.state.fileUploadedSuccesfully){
      return(
        <div>
          <br></br>
          <h4>Il tuo file Ã¨ stato caricato correttamente</h4>
        </div>
      );
    }else{
      return(
        <div>
          <br/>
          <h4>Scegli un file e premi il pulsante Upload</h4>
        </div>
      )
    }
  }

  render() {
    return (
      <div className="container">
        <h2>Sort Student</h2>
        <h3>Elabora file con componente React e API Serverless</h3>
        <div>
          <input type="file" onChange={this.onFileChange} />
          <button onClick={this.onFileUpload}>Upload</button>
        </div>
        {this.fileData()}
      </div>
    )
  }

}

export default App;
