import './App.css';
import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

class App extends Component {
  state = {
    selectedFile: null,
    fileUploadedSuccesfully: false
  }

  onFileChange = event => {
    this.setState({selectedFile: event.target.files[0]});
  }

  onFileUpload = () => {
    const formData = new FormData();
    formData.append(
      "demo file",
      this.state.selectedFile,
      this.state.selectedFile.name
    )
    //chiamare api
    console.log(formData)
    this.setState({selectedFile: null});
    this.setState({fileUploadedSuccesfully: true});
  }

  fileData = () => {
    if(this.state.selectedFile){
      return(
        <div>
        <h2>File Details:</h2>
        <p>File Name: {this.selectedFile.name}</p>
      </div>
      );
    }else if(this.state.fileUploadedSuccesfully){
      return(
        <div>
          <br></br>
          <h4>Il tuo file Ã¨ stato caricato correttamente</h4>
        </div>
      );
    }else{
      return(
        <div>
          <br/>
          <h4>Scegli un file e premi il pulsante Upload</h4>
        </div>
      )
    }
  }

  render() {
    return (
      <div>
        <h2>Sort Student</h2>
        <h3>Elabora file con componente React e API Serverless</h3>
        <div>
          <input type="file" onChange={this.onFileChange} />
          <button onClick={this.onFileUpload}>Upload</button>
        </div>
        {this.fileData()}
        <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>Modal body text goes here.</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">Close</Button>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
      </div>

    )
  }

}

export default App;
