import React, { useState } from 'react';
import { Storage } from 'aws-amplify';

function FileUpload() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  const handleFileUpload = async () => {
    try {
      if (file) {
        // Upload the file to an S3 bucket
        await Storage.put(file.name, file);

        // Trigger the Lambda function to process the file (you will configure this)
        // You can use API Gateway to trigger the Lambda
        // Call your Lambda function here

        alert('File uploaded successfully and sent for processing.');
      } else {
        alert('Please select a file to upload.');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Error uploading file. Please try again.');
    }
  };

  return (
    <div>
      <input type="file" accept=".xlsx,.xls" onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Upload File</button>
    </div>
  );
}

export default FileUpload;
